function createProfile(){const name=document.getElementById("name").value;const age=document.getElementById("age").value;const profession=document.getElementById("profession").value;const hobbies=document.getElementById("hobbies").value.split(",").map(hobby=>hobby.trim());const person={name:name,age:age,profession:profession,hobbies:hobbies};let profile={...person};let addMore=true;while(addMore){const additionalField=prompt("Would you like to add more information (e.g., skills, education)? Type 'no' to finish.");if(additionalField.toLowerCase()==="no"){addMore=false}else{const additionalValue=prompt(`Enter your ${additionalField}:`);profile={...profile,[additionalField]:additionalValue}}}const profileSummary=`
        <h2>Profile Summary:</h2>
        <p><strong>Name:</strong> ${profile.name}</p>
        <p><strong>Age:</strong> ${profile.age}</p>
        <p><strong>Profession:</strong> ${profile.profession}</p>
        <p><strong>Hobbies:</strong> ${profile.hobbies.join(", ")}</p>
        ${Object.entries(profile).filter(([key])=>!["name","age","profession","hobbies"].includes(key)).map(([key,value])=>`<p><strong>${capitalize(key)}:</strong> ${value}</p>`).join("")}
    `;document.getElementById("profileOutput").innerHTML=profileSummary}function capitalize(word){return word.charAt(0).toUpperCase()+word.slice(1)}